var UserIdnetBonusTimer = new function() {

  var self = {
    appId: null,
    serverUrl: null,

    init: function() {
      self.appId = $('body').data('idnet-app-id');
      self.serverUrl = $('body').data('idnet-server-url');

      if ($.cookie('logged_in')) {
        //check if its a game page (appId is only included on game pages)
        if (self.appId && self.serverUrl) {
          self.getPoints();
        // if not on a game, calling directly the service
        } else if ($.cookie('user_pid') && self.serverUrl) {
          //get the value from the cookie for 30 minutes, else get/update it from Idnet server
          if ($.cookie('idnet-bonus-score-value') && $.cookie('idnet-bonus-score-last-update') && ($.cookie('idnet-bonus-score-last-update') > (Date.now() - 0.5*3600000))) {
            self.updateUserInfo($.cookie('idnet-bonus-score-value'));
          } else {
            self.getAllPoints($.cookie('user_pid'));
          }
        }
      } else {
        self.resetScoreWith(0);
      }
    },

    getAllPoints: function(user_pid) {
      if (undefined == user_pid) {
        return false;
      }
      $.ajax({
        type: "GET",
        dataType: 'jsonp',
        jsonpCallback: 'cb',
        url: self.serverUrl + '/api/v1/json/points/total/' + user_pid,
        error: function(request) {
          logConnectionError(request, 'in getPointsFromIdnet');
        },
        success: function(response) {
          if(response && response.points) {
            self.updateUserInfo(response.points);
          }
        }
      });
    },

    //get user info
    getPoints: function() {
      $.ajax({
        type: "GET",
        url: self.serverUrl + '/user',
        xhrFields: { withCredentials: true },
        dataType: 'json',
        data: { app_id: self.appId },
        success: function(response){
          // Update UserInfo if points got
          if(response && response.points && response.points > 0) {
            self.updateUserInfo(response.points);
          }

          //check if user is logged in and check if response contains keys data
          if('logged_in' in response && 'keys' in response && response.logged_in == true) {
            //check if the user hasnt achieved 1_hour_of_play
            if ($.inArray('1_hour_of_play', response.keys) === -1){
              //check if user hasnt achieve 10_minutes_of_play
              if ($.inArray('10_minutes_of_play', response.keys) === -1){
                //start the timer for 10_minutes_of_play first
                setTimeout(function() { self.savePoints('10_minutes_of_play') }, 600000);
              } else {
                //if user has achieved '10_minutes_of_play' then we just start the timer for 1_hour_of_play
                setTimeout(function() { self.savePoints('1_hour_of_play') }, 3600000);
              }
            } else if ($.inArray('10_minutes_of_play', response.keys) === -1) {
              //send request after 10 minutes to save the points
              setTimeout(function() { self.savePoints('10_minutes_of_play') }, 600000);
            }
          }
        }
      });
    },

    //send request to save the points
    savePoints: function(key) {
      $.ajax({
        type: "POST",
        url: self.serverUrl + '/points/bonus_timer',
        xhrFields: { withCredentials: true },
        headers: {'X-Requested-With': 'XMLHttpRequest'},
        dataType: 'json',
        data: { app_id: self.appId, key: key },
        success: function(response){
          if ('success' in response && 'finished' in response && response.success == true && response.finished == false) {
            // Get back the updated score
            self.getAllPoints();

            //user proceed to play more than 10 minutes, so we start the timer for 1_hour_of_play and
            //setup it to 50 minutes (10 from the first timer + 50 now = 60 minutes)
            setTimeout( function() { self.savePoints('1_hour_of_play') }, 3000000);
          } else if ('success' in response && response.success == true) {
            // Get back the updated score
            self.getAllPoints();
          }
        }
      });
    },

    updateUserInfo: function(points) {
      if ((points == 0) || points == '0') {
        $('.js-user-idnet-points').html('0000');
      } else {
        $('.js-user-idnet-points').html(points);
      }

      $.cookie('idnet-bonus-score-last-update', Date.now(), common_cookie_options);
      $.cookie('idnet-bonus-score-value', points, common_cookie_options);
    },

    resetScoreWith: function(newScore) {
      if ((newScore == 0) || points == '0') {
        $('.js-user-idnet-points').html('0000');
      } else {
        $('.js-user-idnet-points').html(newScore);
      }

      $.cookie('idnet-bonus-score-last-update', null, common_cookie_options);
      $.cookie('idnet-bonus-score-value', null, common_cookie_options);
    }
  }

  return self;
}
