(function($) {
  function InfiniteScrolling(container) {
    this.container = $(container);
    this.baseUrl = this.container.data('base-url');
    this.seen = {};
    this.currentPage = 1;
    this.maxPage = parseInt(this.container.data('max-page'));
    this.processing = false;

    if (isNaN(this.maxPage)) {
      this.maxPage = 50;
    }

    this.hidePagination();
    this.filterCurrentItems();
    this.requestNextPage();

    $(window).scroll(this.nextPageIfCloseToBottom.bind(this));
    this.nextPageIfCloseToBottom();
  }

  InfiniteScrolling.prototype = {
    hidePagination: function() {
      $('.navigator').hide();
    },

    filterCurrentItems: function() {
      var items = this.container.find('.item'),
          seen = this.seen;

      items.each(function() {
        var item_id = $(this).data('item-id');
        seen[item_id] = true;
      });
    },

    requestNextPage: function() {
      var itemsQty = this.container.find('.item:visible').length;
      if (itemsQty < 40) {
        this.nextPage();
      }
    },

    appendContent: function(items) {
      var seen = this.seen;

      var $items = $(items).filter(function() {
        var elem = $(this),
            itemId = elem.data('item-id');

        if (seen[itemId]) {
          return false;
        } else {
          seen[itemId] = true;
          return true;
        }
      }).map(function() {
        var elem = $(this),
            image = elem.find('img');
        image.lazyload({
          threshold : 200,
          failure_limit : 5,
          skip_invisible : false
        });
        return elem.get(0);
      });

      // Masonry Grid is used on Videotime
      if ($('.grid').length) {
        $('.grid').append($items).masonry('appended', $items);
      } else {
        $items.appendTo(this.container);
      }

      $(window).resize();
      $(document).trigger("refreshCompatibilityFilter");
      $(document).trigger("refreshParentalFilter");

      if (BrowserDetector.mobile) {
        MobileRedirection.changeItemsLinks();
      }
    },

    newPageLoaded: function(data) {
      var items = $(data).find(".item.thumb");

      $('#loadmoreajaxloader').hide();

      if (items.length <= 0) {
        // No more data: we reached the latest page.
        this.maxPage = this.currentPage;
      } else {
        this.appendContent(items);

        if (typeof(GirlsGamesFilter) != 'undefined') {
          // Applying girls-games filters if any was triggered
          GirlsGamesFilter.applyGirlsGamesFilters(
            items,
            this.container,
            'toggle-girls-games',
            'true'
          );

          GirlsGamesFilter.applyGirlsGamesFilters(
            items,
            this.container,
            'toggle-nongirls-games',
            'false'
          );
        }

        // Job done.
        this.processing = false;
        this.nextPageIfCloseToBottom();
      }
    },

    nextPage: function() {
      // Already loading content: do nothing.
      if (this.processing) { return false }

      // Maximum page reached: do nothing.
      if (this.maxPage <= this.currentPage) { return false }

      this.processing = true;
      this.currentPage += 1;

      $('#loadmoreajaxloader').show();

      $.ajax({
        url: this.baseUrl,
        timeout: 10000,
        data: {
          page: this.currentPage
        },
        success: this.newPageLoaded.bind(this),
        error: function(xhr, textStatus, errorThrown) {
          // Dealing with generic errors
          this.processing = false;
          ErrorHandler.sendReportFor(this.url, xhr.status, 'InfiniteScrollingXHR', '', textStatus, 'retrying-false');
        }
      });
    },

    nextPageIfCloseToBottom: function() {
      var LOAD_TRIGGER = 250;
      if ($(window).scrollTop() > $(document).height() - $(window).height() - LOAD_TRIGGER) {
        this.nextPage();
      }
    }
  };

  $.fn.infiniteScrolling = function() {
    this.each(function() {
      new InfiniteScrolling(this);
    });

    return this;
  };

  $(document).ready(function() {
    $('[data-infinite-scrolling="true"][data-base-url]').infiniteScrolling();
  });
}(jQuery));

var GirlsGamesFilter = (function() {
  var init = function(mainContainer) {
    $(document).ready(function() {

      // there are two filter switchers on page -
      // 1). Hide girls games/Show girls games
      // 2). Show only girls games/Show all
      $('.girls-games-filter').click(function() {
        switchMainContainerFilter('toggle-girls-games', mainContainer);
        toggleGirlsGamesByRule($('.item'), 'true', mainContainer);
        toggleSwitcherButtons($('#show-girls-games'), $('#hide-girls-games'));
      });
      $('#show-only-girls-games').click(function() {
        // reset all previous filters if any was applied
        resetFilters();
        switchMainContainerFilter('toggle-nongirls-games', mainContainer);
        // hide all non-girls games
        toggleGirlsGamesByRule($('.item'), 'false', mainContainer);
        // set up switcher button
        toggleSwitcherButtons($('#show-only-girls-games'), $('#show-all'));

        // since we have two filter buttons
        // they should interact with each other, to not confuse user
        // once user clicks on 'show only girls games', we should set up
        // 'hide/show' filter button properly
        if ($('#show-girls-games').is(':visible')) {
          toggleSwitcherButtons($('#show-girls-games'), $('#hide-girls-games'));
        }
      });

      $('#show-all').click(function() {
        resetFilters();
        toggleSwitcherButtons($('#show-all'), $('#show-only-girls-games'));
      });
    });
  };

  var toggleGirlsGamesByRule = function(items, rule, mainContainer) {

    // 'rule' value defines which type of games will be filtered out:
    // `true` - filter in/out all girls games(Show girls games/Hide girls games)
    // `false` - filter in/out all non-girls games(Show only girls games/Show all)
    items.each(function(index) {
      var itemId = $(this).data('item-id');
      if ($('#for-girls-' + itemId).val() == rule) {
        $(this).toggle();
      };
    });
    // load another portion of items if after filters only few left
    mainContainer.infiniteScrolling();
  };

  var switchMainContainerFilter = function(dataAttributeName, mainContainer) {
    // To apply girls games filter on all pages - writing custom data attribute
    // which can be checked when new page has been loaded(using it in infinite_scrolling.js)
    var filter = mainContainer.data(dataAttributeName);
    mainContainer.data(dataAttributeName, !filter);
  };

  var applyGirlsGamesFilters = function(items, container, switcher, flag) {
    if (container.data(switcher) == true) {
      toggleGirlsGamesByRule(items, flag, container);
    }
    return false;
  };

  var resetFilters = function() {
    $('.item').each(function(item) {
      $(this).show();
    });
  };

  var toggleSwitcherButtons = function(button1, button2) {
    button1.toggle();
    button2.toggle();
  };

  return {
    init: init,
    applyGirlsGamesFilters: applyGirlsGamesFilters
  };
})();
