var SortBy = {
  init: function(selector) {
    this.$selector = $(selector);

    this.setup();
  },

  setup: function() {
    this.$selector.change(this.location.bind(this));
  },

  location: function() {
    var location = window.location.pathname;

    if (this.sort()) {
      location += this.appendParam(window.location.search, 'order', this.sort());
    }

    return window.location = location;
  },

  sort: function() {
    return this.$selector.children('option:selected').val();
  },

  appendParam: function(query, key, value) {
    var hash;
    var i;
    var re;
    var separator;

    i = query.indexOf('#');
    hash = i === -1 ? '' : query.substr(i);
    query = i === -1 ? query : query.substr(0, i);
    re = new RegExp('([?&])' + key + '=.*?(&|$)', 'i');
    separator = query.indexOf('?') !== -1 ? '&' : '?';

    if (query.match(re)) {
      return query.replace(re, '$1' + key + '=' + value + '$2') + hash;
    }

    return query + separator + key + '=' + value + hash;
  }
};

