// A small subset of id.net social features for y8 new design
var UserFollowing = (function() {
  'use strict';

  var elements = [];

  var msg = {
    friend: 'Friends',
    followee: 'Friend',
    requestSent: 'Friend request sent',
  };

  var init = function() {
    if (!(new UserInfo).isLoggedIn()) {
      return;
    }

    elements = collectFollowButtons();
    checkStatus();
  };

  var collectFollowButtons = function() {
    var collected = []
    $('a.follow').each(function() {
      collected.push({
        'authorizationId': $(this).data('authorization-id'),
        'element': $(this)
      });

      $(this).bind('click', function(event) {
        event.preventDefault();
        var idnetUid = $(this).data('authorization-id');

        if (!idnetUid) {
          // This is the case when user is not on y8 (via leaderboards, etc)
          var path = window.location.pathname.split('/');

          // Let's try to get her id.net identity id from url /users/<id>/whatever
          if (path.length >=3 && path[2].length == 24) {
            idnetUid = path[2];
          }
        }

        if (idnetUid) {
          follow(idnetUid);
        } else {
          console.warn(
            "Can't follow without idnet uid",
            window.location.pathname,
            $(this).data('authorization-id')
          );
        }
      });
    });
    return collected;
  }

  var checkStatus = function() {
    var ids = $.map(elements, function(el) {
      return el.authorizationId;
    });

    $.ajax({
      url: '/ajax/social/statuses',
      type: 'POST',
      data: {'authorization_ids': ids},
      timeout: 10000,
      success: onCheckStatusSuccess,
      error: function(request, text, error) {
        console.log("Failed to check friendship status", text, error);
      }
    });
  }

  var onCheckStatusSuccess = function(data) {
    $.each(elements, function(i, v) {
      if (data[v.authorizationId]) {
        // Status may be followee, friend or null
        v.element.removeClass('btn-red').addClass('btn-grey already-friend');
        v.element.find('.follow-btn-text').html(msg[data[v.authorizationId]]);
        v.element.unbind('click');
      }
    });
  };

  var follow = function(idnet_uid) {
    $.ajax({
      url: '/ajax/social/follow/' + idnet_uid,
      type: 'POST',
      timeout: 10000,
      success: onFollowSuccess,
      error: onFollowError
    });
  };

  var onFollowSuccess = function(data) {
    if (data.error) {
      var message = data.error;

      if (data.error == 'link.follow.already_sent') {
        message = 'Following request already sent';
      }

      $('#flash-alert')
        .html(message)
        .fadeIn()
        .delay(5000)
        .fadeOut();
    }

    if (data.status == 'ok') {
      $('#flash-notice')
        .html(msg.requestSent)
        .fadeIn()
        .delay(5000)
        .fadeOut();

      checkStatus();
    }
  };


  var onFollowError = function(request, text, error) {
    console.log(request, text, error);
    $('#flash-alert')
      .html('Following failed, unknown error')
      .fadeIn()
      .delay(5000)
      .fadeOut();
    console.log('Following failed ', text, error);
  };

  return {
    init: init
  };
})();



