// align tags thumbs by columns
// refresh on window resize
var didResize;
$(window).resize(function() {
  didResize = true;
});

// implement on page ready
$(document).ready(function() {

  setInterval(function() {
    if (didResize) {
      $container.isotope('destroy');// destroy
      resize_tags_height();
      isotope_tags();// re-initialize
      didResize = false;
    }
  }, 250);

  resize_tags_height();
  isotope_tags();// init isotope
  $container.isotope('unbindResize');
});

// init isotope
function isotope_tags(){
  $container = $('ul.tag').isotope({
    // options
    itemSelector: 'ul.tag li',
    layoutMode: 'cellsByColumn',
    cellsByColumn: {
      columnWidth: Math.floor($('ul.tag li').outerWidth(true)),
      rowHeight: $('ul.tag li').outerHeight(true)
    }
  });
}

// calculating height of thumbs block
function resize_tags_height(){
  var tags_width = $('ul.tag').width();
  var tags_count = $('ul.tag li').length;
  var tag_width = Math.floor($('ul.tag li').outerWidth(true));
  var tag_height = $('ul.tag li').outerHeight(true);
  var tag_per_line = Math.floor(tags_width / tag_width);
  var tags_lines = Math.ceil(tags_count / tag_per_line);
  var tags_height = tags_lines * tag_height;
  $('ul.tag').height(tags_height);
}
