jQuery(function($) {
  if (BrowserDetector.chrome && BrowserDetector.version >= 40 && $('body[data-controller=Items][data-action=show]').length) {
    var initTime = Date.now();
    var oblTriggered = false;
    var focusOnGame = false;
    var iframeGame = $('#item-container iframe').length ? true : false;

    // Flash and Unity games.
    //
    // Two things happen when OBL is triggered:
    //
    // 1.Mouse has focus on game
    // 2.webkitHidden becomes true since click opens a new tab and user looses the
    //   focus on the game page tab
    //
    // webkitHidden:
    // https://developer.mozilla.org/en-US/docs/Web/Guide/User_experience/Using_the_Page_Visibility_API
    //
    function outboundFromFlashOrUnity() {
      return typeof document.webkitHidden !== 'undefined' &&
        document.webkitHidden &&
        !oblTriggered &&
        focusOnGame &&
        !iframeGame;
    }

    // Iframed games
    // While mouse is over the iframe, check if webkitHidden is true
    function outboundFromIframe() {
      return typeof document.webkitHidden !== 'undefined' &&
        document.webkitHidden &&
        focusOnGame &&
        iframeGame;
    }

    //Track mouse focus
    $('#item-container iframe, #item-container embed, #item-container object').hover(function() {
      focusOnGame = true;
    }, function() {

      focusOnGame = false;
    });

    $('#item-container embed, #item-container object').on('mouseup', function() {
      setTimeout(function(){
        if (outboundFromFlashOrUnity()) {
          var outTime = Date.now() - initTime;
          $.post('/tracking/outbound_link', { tog: outTime }); // notify the server through ajax request.
          oblTriggered = true;
        }
      }, 1000);
    });

    $(document).on('webkitvisibilitychange', function(event) {
      if (outboundFromIframe()) {
        var outTime = Date.now() - initTime;
        $.post('/tracking/outbound_link', { tog: outTime }); // notify the server through ajax request.
        $(this).off(event);
      }
    });
  }
});
