var AvatarChooser = (function() {
  var selected = null;
  var callback = null;
  var pid = null;
  var idnet = null;
  var modalInitialized = false;

  // Avatar ids as defined in id.net (ProfilePicturePreset)
  var AVATARS = {
    male: 1,
    female: 2,
    monkey: 3,
    greenmonster: 4,
    bluemonster: 5
  };

  var init = function(cb) {
    callback = cb;
    idnet = $('body').data('idnet-server-url');
    pid = $.cookie('user_pid');

    var avatarPresent = $('.avatar').prop('src').indexOf('default') === -1;

    if (avatarPresent || !pid) {
      // We see that user has an avatar present (by looking at header).
      // In this case, just continue with posting a comment.
      return callback();
    }

    // check if user has an avatar or preset in id.net
    $.ajax({
      url: idnet + '/profiles/' + pid + '.json',
      timeout: 10000
    })
    .done(function(data) {
      if (data && data.profile && data.profile.avatar_present != true) {
        // no preset, show avatar selection modal
        display();
      } else {
        // just continue with posting
        callback();
      }
    })
    .fail(function() {
      // just try to post the message, we might have network issues...
      callback();
    });
  };

  var display = function() {
    if (!modalInitialized) {
      initModal();
    }

    $('.choose-avatar-popup').find('.error').hide();
    $('.choose-avatar-popup').show();
  };

  var initModal = function() {
    initCloseButton();
    initSelectableAvatars();
    $('.btn-confirm').click(chooseAvatar);
    $('.btn-upload').click(uploadAvatar);
    modalInitialized = true;
  };

  var initSelectableAvatars = function() {
    $('.avatar-preset-wrapper').bind('click', function() {
      var clicked = $(this).data('id');

      if (selected == clicked) {
        selected = null;
        $(this).removeClass('active');
        $('.btn-confirm').addClass('disabled');
        $('.avatar-preset-wrapper').removeClass('dimmed');
      } else if (clicked) {
        selected = clicked;
        $('.avatar-preset-wrapper').removeClass('active');
        $(this).addClass('active');
        $(this).removeClass('dimmed');
        $('.avatar-preset-wrapper:not(.active)').addClass('dimmed');
        $('.btn-confirm').removeClass('disabled');
      }
    });
  };

  var initCloseButton = function() {
    $('.choose-avatar-popup .btn-close').click(function(e) {
      e.preventDefault();
      $('.choose-avatar-popup').hide();
    });
  };

  var chooseAvatar = function(e) {
    e.preventDefault();
    if (!selected) {
      return;
    }

    $.ajax({
      url: '/ajax/choose_avatar_preset',
      data: { choise: AVATARS[selected.replace('-', '')] },
      method: 'POST',
      timeout: 10000
    })
    .done(function(data) {
      if (data.success == true) {
        callback();
        $('.choose-avatar-popup').hide();
      }
    })
    .fail(function() {
      $('.choose-avatar-popup').find('.error').show();
    });
  };

  var uploadAvatar = function(e) {
    e.preventDefault();
    var url = idnet + '/' + pid + '/profile_picture/new?external=1';
    window.open(url, '_blank');
    window.focus();
    $('.choose-avatar-popup').hide();
    callback();
  };

  return {
    init: init
  };
})();

